import React from 'react';
import { Card, Button } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';
import HeaderTransaction from './HeaderTransaction';
import Email from './Customer/Email';
import IdPsp from './Customer/IdPsp';
import FirstName from './Customer/FirstName';
import LastName from './Customer/LastName';
import background from '../images/background.jpg';

export default class Payment extends React.Component {
	constructor() {
		super();
		this.state = {
			card: '',
			amount: '',
			idPsp: '',
			errorMessage: ''
		};
	}

	checkedEmail = (email) => {
		this.setState({ email: email });
		console.log('email', email);
	};

	firstName = (firstName) => {
		this.setState({ firstName: firstName });
		console.log('firstName', firstName);
	};

	lastName = (lastName) => {
		this.setState({ lastName: lastName });
		console.log('lastName', lastName);
	};

	handleSubmit = () => {
		this.setState({ idPsp: this.props.location.state.idPsp });
		// We want to save a transaction in our db
		// Here, we are using ES6 syntax, so we do not need to store this in ctx.
		if (this.state.firstName && this.state.lastName && this.state.email && this.props.location.state) {
			fetch(
				'http://localhost:3000/doTransaction',
				{
					method: 'POST',
					headers: {
						'Content-Type': 'application/x-www-form-urlencoded'
					},
					body: `cardtype=${this.props.location.state.card}&amount=${this.props.location.state
						.amount}&idPsp=${this.props.location.state.idPsp}&paymentMode=${this.props.location.state
						.paymentMode}&firstName=${this.state.firstName}&lastName=${this.state.lastName}&email=${this
						.state.email}`
				},
				this.props.history.push('/Summary', this.state)
			).catch((error) => {
				console.error(error);
			});
		}
	};

	render() {
		const style = {
			display: {
				marginTop: 50,
				textAlign: 'center',
				color: '#d63031'
			}
		};
		return (
			<div style={{ backgroundImage: `url(${background})`, width: '100%', height: '100vh' }}>
				<div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
					<Card
						style={{
							marginTop: '40px',
							backgroundColor: '#F3F5F7',
							width: '30rem'
						}}
					>
						<HeaderTransaction />
						<p style={{ color: 'grey', textAlign: 'center' }}>
							Information du client qui utilisera ce lien de paiement
						</p>
						<p
							style={{
								color: '#50C3B9',
								fontWeight: 'bold',
								fontSize: 'large',
								textDecoration: 'underline'
							}}
						>
							Le client (destinataire)
						</p>
						<Email checkedEmail={this.checkedEmail} />
						<IdPsp idPsp={this.props.location.state.idPsp} />
						<FirstName firstName={this.firstName} />
						<LastName lastName={this.lastName} />
						<div style={{ display: 'flex', justifyContent: 'space-around' }}>
							<Button style={{ backgroundColor: '#C82333', border: 0 }}>Annuler</Button>
							<Button style={{ backgroundColor: '#218838', border: 0 }} onClick={this.handleSubmit}>
								Continuer
							</Button>
						</div>
						<p style={style.display}>{this.state.errorMessage}</p>
					</Card>
				</div>
			</div>
		);
	}
}
