import React from 'react';
import { Card, CardImg, CardText, CardBody, CardTitle, CardSubtitle, Button } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';
import HeaderTransaction from './HeaderTransaction';
import background from '../images/background.jpg';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronRight } from '@fortawesome/free-solid-svg-icons';

export default class Summary extends React.Component {
	constructor() {
		super();
		this.state = {};
	}

	handleSubmit = () => {
		// We want to save a transaction in our db
		// Here, we are using ES6 syntax, so we do not need to store this in ctx.
		if (this.state.firstName && this.state.lastName && this.state.email && this.props.location.state) {
			fetch(
				'http://localhost:3000/doTransaction',
				{
					method: 'POST',
					headers: {
						'Content-Type': 'application/x-www-form-urlencoded'
					},
					body: `cardtype=${this.props.location.state.card}&amount=${this.props.location.state
						.amount}&idPsp=${this.props.location.state.idPsp}&paymentMode=${this.props.location.state
						.paymentMode}&firstName=${this.state.firstName}&lastName=${this.state.lastName}&email=${this
						.state.email}`
				},
				this.props.history.push('/Summary', this.state)
			).catch((error) => {
				console.error(error);
			});
		}
	};

	render() {
		return (
			<div style={{ backgroundImage: `url(${background})`, width: '100%', height: '100vh' }}>
				<div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
					<Card
						style={{
							marginTop: '40px',
							backgroundColor: '#F3F5F7',
							width: '30rem'
						}}
					>
						<HeaderTransaction />
						<p
							style={{
								color: '#50C3B9',
								fontWeight: 'bold',
								fontSize: 'large',
								textDecoration: 'underline'
							}}
						>
							Récapitulatif
						</p>

						<Card style={{ backgroundColor: '#e8e9ea' }}>
							<CardBody style={{ color: 'grey' }}>
								<CardTitle style={{ fontWeight: 'bold', marginBottom: '10px' }}>Le paiement</CardTitle>
								<CardSubtitle style={{ marginBottom: '5px' }}>Moyen de paiement :</CardSubtitle>
								<CardSubtitle style={{ marginBottom: '5px' }}>Mode de paiement :</CardSubtitle>
								<CardSubtitle style={{ marginBottom: '5px' }}>Montant :</CardSubtitle>
								<CardSubtitle style={{ marginBottom: '5px' }}>Client(e) :</CardSubtitle>
							</CardBody>
						</Card>
					</Card>
				</div>
			</div>
		);
	}
}
