import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';

export default class Footer extends React.Component {
	constructor() {
		super();
		this.state = {
			total: ''
		};
	}

	async componentDidMount() {
		var ctx = this;
		console.log('hey', ctx.props.idPsp);
		if (this.props.idPsp) {
			fetch(`http://localhost:3000/summarypayment?idPsp=${ctx.props.idPsp}`)
				.then(function(res, err) {
					res.json();
				})
				.then(function(data) {
					// ctx.setState({ total: data });
					console.log(data);
				})
				.catch(function(err) {
					console.log(err);
				});
		}
	}

	render() {
		console.log(this.props);
		return (
			<div
				style={{
					backgroundColor: '#35AD74',
					width: '100%',
					height: '5rem',
					display: 'flex',
					alignItems: 'center'
				}}
			>
				<span style={{ color: 'white', fontWeight: 'bold' }}>Total</span>
				<span>{this.state.total}</span>
			</div>
		);
	}
}
