import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { FormGroup, Label, CustomInput } from 'reactstrap';

export default class CardItem extends React.Component {
	handleClick = (title) => {
		this.props.handleClickParent(title);
	};
	render() {
		console.log(this.state.idCardFound);
		const cardAvailable = [
			{
				title: 'Visa',
				img: require('../images/Visa.png')
			},
			{
				title: 'MasterCard',
				img: require('../images/Mastercard.png')
			},
			{
				title: 'AmericanCard',
				img: require('../images/American-Express.png')
			}
		];
		let cardList = [];
		for (let i = 0; i < cardAvailable.length; i++) {
			cardList.push(
				<CustomInput
					type="radio"
					key={i}
					id={i}
					name="customRadio"
					onClick={() => this.handleClick(cardAvailable[i].title)}
					// checked={
					// 	this.state.idCardFound !== null &&
					// 	this.state.idCardFound === 'Visa' &&
					// 	cardAvailable.title === 'Visa' ? (
					// 		true
					// 	) : (
					// 		false
					// 	)
					// }
				>
					<img
						alt={cardAvailable[i].title}
						key={i}
						src={cardAvailable[i].img}
						title={cardAvailable[i].title}
						style={{ width: '64px', height: '40' }}
					/>
				</CustomInput>
			);
		}
		return (
			<div>
				<FormGroup>
					<Label style={{ color: 'grey' }}>Votre type de carte</Label>

					<div style={{ display: 'flex', justifyContent: 'space-around' }}>{cardList}</div>
				</FormGroup>
			</div>
		);
	}
}
