import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { FormGroup, Label, Input } from 'reactstrap';

export default class BeneficiaryClient extends React.Component {
	constructor() {
		super();
		this.state = {
			ibanValue: ''
		};
	}
	handleCheckIban = ({ target: { value } }) => {
		value = value
			.toUpperCase()
			// Remove all non-digits
			.replace(
				/[a-zA-Z0-9]{2}\s?([0-9]{4}\s?){2}([0-9]{2})([a-zA-Z0-9]{2}\s?)([a-zA-Z0-9]{4}\s?){2}([a-zA-Z0-9]{1})([0-9]{2})\s?/,
				''
			)
			// Stick to first 16, ignore later digits
			.slice(0, 18)
			// Add a space after any 4-digit group followed by more digits
			.replace(/(\d{4})(?=\d)/g, '$1 ');

		// Stick to first 16, ignore later digits

		this.setState({ ibanValue: value });
	};
	render() {
		return (
			<FormGroup>
				<Label style={{ color: 'grey' }}>Num√©ro IBAN</Label>
				<Input
					type="text"
					name="iban"
					id="iban"
					placeholder="IBAN"
					required
					value={this.state.ibanValue}
					onChange={this.handleCheckIban}
				/>
			</FormGroup>
		);
	}
}
