import React from 'react';
import { Card } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';
import HeaderTransaction from './HeaderTransaction';
import background from '../images/background.jpg';
import SummaryPayment from './Summary/SummaryPayment';

export default class Summary extends React.Component {
	constructor() {
		super();
		this.state = {
			card: '',
			amount: '',
			idPsp: '',
			errorMessage: ''
		};
	}

	handleId = (id) => {
		console.log(id);
	};

	render() {
		console.log(this.props);
		return (
			<div style={{ backgroundImage: `url(${background})`, width: '100%', height: '100vh' }}>
				<div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
					<Card
						style={{
							marginTop: '40px',
							backgroundColor: '#F3F5F7',
							width: '30rem'
						}}
					>
						<HeaderTransaction />
						<p
							style={{
								color: '#50C3B9',
								fontWeight: 'bold',
								fontSize: 'large',
								textDecoration: 'underline'
							}}
						>
							RÃ©capitulatif
						</p>
						<SummaryPayment idPsp={this.props.location.state} id={this.handleId} />
					</Card>
				</div>
			</div>
		);
	}
}
