import React from 'react';
import { Button, Form, FormGroup, Label, Input, Card, InputGroupText, InputGroupAddon, InputGroup } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';
import HeaderTransaction from './HeaderTransaction';
import background from '../images/background.jpg';

export default class Transaction extends React.Component {
	constructor() {
		super();
		this.state = {
			bill: '',
			lastName: '',
			firstName: '',
			cardNumber: '',
			cryptoNumber: '',
			expiredMonth: '',
			expiredYear: ''
		};
	}
	handleSubmit = () => {
		// We want to save a transaction in our db
		// Here, we are using ES6 syntax, so we do not need to store this in ctx.
		fetch('http://localhost:3000/doTransaction', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/x-www-form-urlencoded'
			},
			body: `first_name=${this.state.firstName}&last_name=${this.state.lastName}`
		}).catch((error) => {
			console.error(error);
		});
	};

	handleChange = ({ target: { value } }) => {
		value = value
			// Remove all non-digits
			.replace(/\D+/, '')
			// Stick to first 16, ignore later digits
			.slice(0, 18)
			// Add a space after any 4-digit group followed by more digits
			.replace(/(\d{4})(?=\d)/g, '$1 ');
		this.setState({ value });
	};

	render() {
		//Afficher le nombre de mois dans le input expiredMonth
		let months = [];
		let expiredYear = [];
		for (let i = 1; i < 13; i++) {
			months.push(<option key={i}>{i}</option>);
		}
		for (let i = new Date().getFullYear(); i < new Date().getFullYear() + 4; i++) {
			expiredYear.push(<option key={i}>{i}</option>);
		}
		return (
			<div style={{ backgroundImage: `url(${background})`, width: '100%', height: '100vh' }}>
				<div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
					<Card
						style={{
							marginTop: '40px',
							backgroundColor: '#F3F5F7',
							width: '30rem'
						}}
					>
						<HeaderTransaction />
						<Form style={{ marginTop: '5px' }}>
							<FormGroup style={{ marginBottom: '10px' }}>
								<Label style={{ color: 'grey' }}>Montant</Label>
								<InputGroup>
									<Input
										type="number"
										name="bill"
										placeholder="Montant"
										required
										onChange={(e) => this.setState({ bill: e.target.value })}
									/>

									<InputGroupAddon addonType="prepend">
										<InputGroupText>€</InputGroupText>
									</InputGroupAddon>
								</InputGroup>
								{isNaN(this.state.bill) ? (
									<p style={{ color: '#C82333', textAlign: 'center' }}>Ceci n'est pas un nombre</p>
								) : null}
							</FormGroup>
							<FormGroup>
								<Label style={{ color: 'grey' }}>Nom</Label>
								<Input
									type=""
									name="lastName"
									id="lastName"
									placeholder="Nom"
									required
									onChange={(e) => this.setState({ lastName: e.target.value })}
								/>
								{this.state.lastName.match(/\d/) ? (
									<p style={{ color: '#C82333', textAlign: 'center' }}>Ceci n'est pas une lettre</p>
								) : null}
							</FormGroup>
							<FormGroup>
								<Label style={{ color: 'grey' }}>Prénom</Label>
								<Input
									type="firstName"
									name="firstName"
									id="firstName"
									placeholder="Prénom"
									required
									onChange={(e) => this.setState({ firstName: e.target.value })}
								/>
								{this.state.firstName.match(/\d/) ? (
									<p style={{ color: '#C82333', textAlign: 'center' }}>Ceci n'est pas une lettre</p>
								) : null}
							</FormGroup>
							<Label style={{ color: 'grey' }}>Numéro de carte</Label>
							<FormGroup style={{ display: 'flex', paddindRight: '10px' }}>
								<Input
									type="number"
									name="cardNumber"
									id="cardNumber"
									placeholder="Numéro de carte"
									required
									value={this.state.value}
									onChange={this.handleChange}
								/>
							</FormGroup>
							<div
								className="form-inline"
								style={{ justifyContent: 'space-around', marginBottom: '10px' }}
							>
								<FormGroup style={{ justifyContent: 'flex-start' }}>
									<Label style={{ color: 'grey' }}>Expire fin: &nbsp;</Label>
									<Input
										type="select"
										name="select"
										id="expiredMonth"
										required
										onChange={(e) => this.setState({ expiredMonth: e.target.value })}
									>
										{months}
									</Input>
									&nbsp;/&nbsp;
									<Input
										type="select"
										name="yeexpiredYear"
										id="expiredYear"
										required
										onChange={(e) => this.setState({ expiredYear: e.target.value })}
									>
										{expiredYear}
									</Input>
								</FormGroup>
							</div>
							<FormGroup>
								<Label style={{ color: 'grey' }}>Cryptogramme visuel</Label>
								<Input
									type="number"
									name="cryptoNumber"
									id="cryptoNumber"
									placeholder="Cryptogramme visuel"
									required
									onChange={(e) => this.setState({ cryptoNumber: e.target.value })}
								/>
								{isNaN(this.state.cryptoNumber) || this.state.cryptoNumber.length > 3 ? (
									<p style={{ color: '#C82333', textAlign: 'center' }}>
										Ceci n'est pas un nombre ou le nombre de chiffre est trop élevé (3)
									</p>
								) : null}
							</FormGroup>
							<div style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '30px' }}>
								<Button style={{ backgroundColor: '#C82333', border: 0 }}>Annuler</Button>
								<Button style={{ backgroundColor: '#218838', border: 0 }} onClick={this.handleSubmit}>
									Valider
								</Button>
							</div>
						</Form>
					</Card>
				</div>
			</div>
		);
	}
}
