import React from 'react';
import { Table, Card } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';
import background from '../images/background.jpg';

export default class TableTransactions extends React.Component {
	constructor() {
		super();
		this.state = {
			expiredMonth: '',
			expiredYear: '',
			cardNumber: '',
			cryptoNumber: '',
			idPsp: '',
			total: ''
		};
	}

	componentDidMount() {
		var ctx = this;
		console.log('signin from front handled...');

		fetch(`http://localhost:3000/allTransactions`)
			.then(function(res, err) {
				return res.json();
			})
			.then(function(data) {
				console.log(data);
				// ctx.setState({
				//     paymentMode: data.customer.paymentMode,
				//     cardType: data.customer.cardtype,
				//     imgCardType: require(`../images/${data.customer.cardtype}.png`),
				//     amount: data.customer.amount,
				//     firstName: data.customer.firstName,
				//     lastName: data.customer.lastName,
				//     href: data.customer.href
				// });
				console.log(data);
			})
			.catch(function(err) {
				console.log(err);
			});
	}

	render() {
		return (
			<div style={{ backgroundImage: `url(${background})`, width: '100%', height: '100vh' }}>
				<Card
					style={{
						backgroundColor: '#F3F5F7'
					}}
				>
					<p
						style={{
							color: '#50C3B9',
							fontWeight: 'bold',
							fontSize: 'large',
							textDecoration: 'underline'
						}}
					>
						Toutes les transactions
					</p>
					<Table striped>
						<thead>
							<tr>
								<th>ID</th>
								<th>Référencee</th>
								<th>Montant</th>
								<th>Mode de P.</th>
								<th>Moyen de P.</th>
								<th>Statut</th>
								<th>Client</th>
							</tr>
						</thead>
						<tbody>
							<tr>
								<th scope="row">1</th>
								<td>Mark</td>
								<td>Otto</td>
								<td>@mdo</td>
							</tr>
						</tbody>
					</Table>
				</Card>
			</div>
		);
	}
}
