import React from 'react';
import { Button, Form, FormGroup, Label, Input, Card, InputGroupText, InputGroupAddon, InputGroup } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';
import HeaderTransaction from './HeaderTransaction';
import Amount from './Amount';
import CardItem from './CardItem';
import BeneficiaryClient from './BeneficiaryClient';
import background from '../images/background.jpg';

export default class Payment extends React.Component {
	constructor() {
		super();
		this.state = {
			amount: ''
		};
	}
	handleSubmit = () => {
		// We want to save a transaction in our db
		// Here, we are using ES6 syntax, so we do not need to store this in ctx.
		fetch('http://localhost:3000/doTransaction', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/x-www-form-urlencoded'
			},
			body: `first_name=${this.state.firstName}&last_name=${this.state.lastName}`
		}).catch((error) => {
			console.error(error);
		});
	};

	handleAmount = (amount) => {
		this.setState({ amount: amount });
	};

	render() {
		console.log('amount:', this.state.amount);
		return (
			<div style={{ backgroundImage: `url(${background})`, width: '100%', height: '100vh' }}>
				<div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
					<Card
						style={{
							marginTop: '40px',
							backgroundColor: '#F3F5F7',
							width: '30rem'
						}}
					>
						<p style={{ color: '#50C3B9', fontWeight: 'bold', fontSize: 'large' }}>Le paiement</p>
						<CardItem />
						<Amount handleClickAmount={this.handleAmount} />
					</Card>
				</div>
			</div>
		);
	}
}
