import React from 'react';
import { Card, CardBody, CardTitle, CardSubtitle, Button } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLink } from '@fortawesome/free-solid-svg-icons';

export default class CopyLink extends React.Component {
	constructor() {
		super();
	}

	render() {
		return (
			<div
				style={{
					display: 'flex',
					justifyContent: 'space-around',
					marginTop: '1rem',

					border: ''
				}}
			>
				<span>
					<FontAwesomeIcon size="1x" style={{ color: 'grey', marginRight: '20px' }} icon={faLink} />
					<span style={{ textDecoration: 'underline', textDecorationColor: '#50C3B9' }}>coucou</span>
				</span>
				<Button style={{ borderColor: '#50C3B9', color: '#50C3B9' }} size="sm" outline>
					COPIER
				</Button>
				<div style={{ marginBottom: '5rem', color: 'grey' }}>
					{' '}
					<p>Transmettez ce lien à votre client afin qu'il règle sa commande.</p>
				</div>
			</div>
		);
	}
}
