import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { FormGroup, Label, CustomInput } from 'reactstrap';

export default class CardItem extends React.Component {
	constructor() {
		super();
		this.state = {
			cardSelected: ''
		};
	}
	handleClick = (title) => {
		this.setState({ cardSelected: title });
	};
	render() {
		const cardAvailable = [
			{
				title: 'Visa',
				img: require('../images/Visa.png')
			},
			{
				title: 'MasterCard',
				img: require('../images/Mastercard.png')
			},
			{
				title: 'AmericanCard',
				img: require('../images/American-Express.png')
			}
		];
		let cardList = [];
		for (let i = 0; i < cardAvailable.length; i++) {
			cardList.push(
				<CustomInput type="radio" id={i} name="customRadio" onClick={this.handleClick(cardAvailable.title)}>
					<img
						key={i}
						src={cardAvailable[i].img}
						title={cardAvailable[i].title}
						style={{ width: '64px', height: '40' }}
					/>
				</CustomInput>
			);
		}
		return (
			<div>
				<FormGroup>
					<Label style={{ color: 'grey' }}>Votre type de carte</Label>

					<div style={{ display: 'flex', justifyContent: 'space-around' }}>{cardList}</div>
				</FormGroup>
			</div>
		);
	}
}
