import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { Col, Row, Button, Form, FormGroup, Label, Input, InputGroupText, InputGroupAddon } from 'reactstrap';

export default class BeneficiaryClient extends React.Component {
	constructor() {
		super();
		this.state = {
			ibanValue: ''
		};
	}
	handleCheckIban = ({ target: { value } }) => {
		value = value.toUpperCase();
		// Remove all non-digits

		// Stick to first 16, ignore later digits

		this.setState({ ibanValue: value });
	};
	render() {
		return (
			<div>
				<Label style={{ color: 'grey' }}>Num√©ro IBAN</Label>
				<div style={{ display: 'flex', justifyContent: 'space-around' }}>
					<Row form>
						<Col md={1}>
							<InputGroupAddon addonType="prepend">
								<InputGroupText>FR</InputGroupText>
							</InputGroupAddon>
						</Col>
						<Col md={2}>
							<FormGroup>
								<Input type="text" name="keyIban" id="keyIban" maxLength="2" required />
							</FormGroup>
						</Col>
						<Col md={2}>
							<FormGroup>
								<Input type="text" name="BankCode" id="BankCode" maxLength="4" required />
							</FormGroup>
						</Col>
						<Col md={2}>
							<FormGroup>
								<Input type="text" name="counterCount" id="counterCount" maxLength="4" required />
							</FormGroup>
						</Col>
						<Col md={2}>
							<FormGroup>
								<Input type="text" name="countNumber" id="countNumber" maxLength="4" required />
							</FormGroup>
						</Col>
						<Col md={2}>
							<FormGroup>
								<Input type="text" name="keyRib" id="keyRib" maxLength="2" required />
							</FormGroup>
						</Col>
					</Row>
				</div>
			</div>
		);
	}
}
