import React from 'react';
import { Card, CardBody, CardTitle, CardSubtitle } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAngleRight } from '@fortawesome/free-solid-svg-icons';

export default class SummaryPayment extends React.Component {
	constructor() {
		super();
		this.state = { paymentMode: '', cardType: '', amount: '', firstName: '', lastName: '', imgCardType: '' };
	}

	componentDidMount() {
		var ctx = this;
		console.log('signin from front handled...');

		fetch(`http://localhost:3000/summarypayment?idPsp=${this.props.idPsp}`)
			.then(function(res, err) {
				return res.json();
			})
			.then(function(data) {
				ctx.setState({
					paymentMode: data.customer.paymentMode,
					cardType: data.customer.cardtype,
					imgCardType: require(`../../images/${data.customer.cardtype}.png`),
					amount: data.customer.amount,
					firstName: data.customer.firstName,
					lastName: data.customer.lastName
				});
				console.log(data);
			})
			.catch(function(err) {
				console.log(err);
			});
	}

	render() {
		return (
			<Card style={{ backgroundColor: '#e8e9ea' }}>
				<CardBody style={{ color: 'grey' }}>
					<CardTitle style={{ fontWeight: 'bold', marginBottom: '10px' }}>Le paiement</CardTitle>
					<CardSubtitle style={{ marginBottom: '5px' }}>
						<FontAwesomeIcon size="1x" style={{ marginRight: '5px' }} icon={faAngleRight} />
						Moyen de paiement :
						<span style={{ fontWeight: 'bold', marginLeft: '10px' }}>
							<img
								alt={`carte ${this.state.cardType}`}
								src={this.state.imgCardType}
								title={`${this.state.cardType}`}
								style={{ width: '40px', height: '25' }}
							/>
							{this.state.cardType}
						</span>
					</CardSubtitle>
					<CardSubtitle style={{ marginBottom: '5px' }}>
						<FontAwesomeIcon size="1x" style={{ marginRight: '5px' }} icon={faAngleRight} />Mode de paiement
						: <span style={{ fontWeight: 'bold', marginLeft: '10px' }}>{this.state.paymentMode}</span>
					</CardSubtitle>
					<CardSubtitle style={{ marginBottom: '5px' }}>
						<FontAwesomeIcon size="1x" style={{ marginRight: '5px' }} icon={faAngleRight} />Montant :
						<span style={{ fontWeight: 'bold', marginLeft: '10px' }}>{this.state.amount + ' ' + 'â‚¬'}</span>
					</CardSubtitle>
					<CardSubtitle style={{ marginBottom: '5px' }}>
						<FontAwesomeIcon size="1x" style={{ marginRight: '5px' }} icon={faAngleRight} />Client(e) :
						<span style={{ fontWeight: 'bold', marginLeft: '10px' }}>
							{this.state.firstName + ' ' + this.state.lastName}
						</span>
					</CardSubtitle>
				</CardBody>
			</Card>
		);
	}
}
