import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { FormGroup, Label, CustomInput } from 'reactstrap';

export default class PaymentMethod extends React.Component {
	constructor() {
		super();
		this.state = {};
	}
	handleClickPaymentMode = (paymentMode) => {
		this.props.handleClickCardSelected(paymentMode);
	};
	render() {
		return (
			<div>
				<Label style={{ color: 'grey', fontWeight: 'bold' }}>Mode de paiement</Label>
				<FormGroup style={{ display: 'flex', justifyContent: 'center' }}>
					<CustomInput
						type="radio"
						id="paymentMode"
						name="paymentMode"
						onClick={() => this.handleClickPaymentMode}
					/>
					<span style={{ fontWeight: 'bold', colr: 'grey' }}>Comptant</span>
				</FormGroup>
			</div>
		);
	}
}
