import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';

export default class Footer extends React.Component {
	constructor() {
		super();
		this.state = {
			total: ''
		};
	}

    componentDidMount() {
        var ctx = this;
        console.log('signin from front handled...');

        fetch(`http://localhost:3000/summarypayment?idPsp=${this.props.location.state}`)
            .then(function (res, err) {
                return res.json();
            })
            .then(function (data) {
                ctx.setState({
                    paymentMode: data.customer.paymentMode,
                    cardType: data.customer.cardtype,
                    imgCardType: require(`../images/${data.customer.cardtype}.png`),
                    amount: data.customer.amount,
                    firstName: data.customer.firstName,
                    lastName: data.customer.lastName,
                    href: data.customer.href
                });
                console.log(data);
            })
            .catch(function (err) {
                console.log(err);
            });
    }

	render() {
		console.log(this.props);
		return (
			<div
				style={{
					backgroundColor: '#35AD74',
					width: '100%',
					height: '5rem',
					display: 'flex',
					alignItems: 'center'
				}}
			>
				<span style={{ color: 'white', fontWeight: 'bold' }}>Total</span>
				<span>{this.state.total}</span>
			</div>
		);
	}
}
