import React from 'react';
import { Button, Form, Card } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';
import HeaderTransaction from './HeaderTransaction';
import CardNumber from './CardNumber';
import ExpiredCard from './Transaction/ExpiredCard';
import Footer from './Transaction/Footer';
import CryptoNumberCard from './Transaction/CryptoNumberCard';
import background from '../images/background.jpg';

export default class Transaction extends React.Component {
	constructor() {
		super();
		this.state = {
			expiredMonth: '',
			expiredYear: '',
			cardNumber: '',
			cryptoNumber: '',
			idPsp: ''
		};
	}

	handleSubmit = () => {
		const extractParamsUrl = (chaineGET) => {
			chaineGET = chaineGET.split('&');
			let result = '';

			chaineGET.forEach(function(el) {
				let param = el.split('=');
				param[0] = param[0].replace('?', '');
				result = param[0];
			});

			return result;
		};

		fetch(
			'http://localhost:3000/transaction',
			{
				method: 'POST',
				headers: {
					'Content-Type': 'application/x-www-form-urlencoded'
				},
				body: `idPsp=${extractParamsUrl(this.props.location.search)}&cardNumber=${this.state
					.CardNumber}&cryptogramCard=${this.state.cryptogramCard}&expiredMonth=${this.state
					.expiredMonth}&expiredYear=${this.state.expiredYear}`
			},
			this.setState({ idPsp: extractParamsUrl(this.props.location.search) })
		).catch((error) => {
			console.error(error);
		});
	};

	cryptoNumberCard = (crypto) => {
		this.setState({ cryptoNumber: crypto });
	};

	handleCardValue = (idCard) => {
		console.log(idCard);
		this.setState({ cardNumber: idCard });
	};

	handleClickMonthExpired = (month) => {
		this.setState({ expiredMonth: month });
	};

	handleClickYearExpired = (year) => {
		this.setState({ expiredYear: year });
	};

	render() {
		console.log(this.state.idPsp);
		return (
			<div style={{ backgroundImage: `url(${background})`, width: '100%', height: '100vh' }}>
				<div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
					<Card
						style={{
							marginTop: '40px',
							backgroundColor: '#F3F5F7',
							width: '30rem'
						}}
					>
						<HeaderTransaction />
						<Form style={{ marginTop: '5px' }}>
							<CardNumber handleClickParent={this.handleCardValue} />
							<ExpiredCard
								expiredMonth={this.handleClickMonthExpired}
								expiredYear={this.handleClickYearExpired}
							/>
							<CryptoNumberCard cryptoNumberCard={this.cryptoNumberCard} />
							<div style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '30px' }}>
								<Button style={{ backgroundColor: '#C82333', border: 0 }}>Annuler</Button>
								<Button style={{ backgroundColor: '#218838', border: 0 }} onClick={this.handleSubmit}>
									Valider
								</Button>
							</div>
						</Form>
						<Footer props={this.state.idPsp} />
					</Card>
				</div>
			</div>
		);
	}
}
