import React from 'react';
import { Button, Form, FormGroup, Label, Input, Card, InputGroupText, InputGroupAddon, InputGroup } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';
import HeaderTransaction from './HeaderTransaction';
import CardItem from './CardItem';
import BeneficiaryClient from './BeneficiaryClient';
import background from '../images/background.jpg';

export default class Transaction extends React.Component {
	constructor() {
		super();
		this.state = {
			bill: '',
			lastName: '',
			firstName: '',
			cardNumber: '',
			cryptoNumber: '',
			expiredMonth: '',
			expiredYear: '',
			cardNumberValue: '',
			cryptoNumberValue: '',
			letterFirstNameValue: '',
			letterLastNameValue: '',
			idCardSelected: '',
			idCardFound: ''
		};
	}
	handleSubmit = () => {
		// We want to save a transaction in our db
		// Here, we are using ES6 syntax, so we do not need to store this in ctx.
		fetch('http://localhost:3000/doTransaction', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/x-www-form-urlencoded'
			},
			body: `first_name=${this.state.firstName}&last_name=${this.state.lastName}`
		}).catch((error) => {
			console.error(error);
		});
	};

	handleChangeCardNumber = ({ target: { value } }) => {
		value = value
			// Remove all non-digits
			.replace(/\D+/, '')
			// Stick to first 16, ignore later digits
			.slice(0, 18)
			// Add a space after any 4-digit group followed by more digits
			.replace(/(\d{4})(?=\d)/g, '$1 ');

		this.setState({ cardNumberValue: value });
		if (this.state.cardNumberValue.substr(0) == 4) {
			this.setState({ idCardFound: 'Visa' });
			console.log('ok');
		}
	};

	handleChangeryptoNumber = ({ target: { value } }) => {
		value = value
			// Remove all non-digits
			.replace(/\D+/, '')
			// Stick to first 16, ignore later digits
			.slice(0, 3);

		this.setState({ cryptoNumberValue: value });
	};

	handleChangeFirstNameLetter = ({ target: { value } }) => {
		value = value
			// Remove all non-digits
			.replace(/\d+/g, '');

		this.setState({ letterFirstNameValue: value });
	};

	handleChangeLastNameLetter = ({ target: { value } }) => {
		value = value
			// Remove all non-digits
			.replace(/\d+/g, '');

		this.setState({ letterLastNameValue: value });
	};

	handleClickCard = (idCard) => {
		console.log(idCard);
		this.setState({ idCardSelected: idCard });
	};

	render() {
		//Afficher le nombre de mois dans le input expiredMonth
		let months = [];
		let expiredYear = [];
		for (let i = 1; i < 13; i++) {
			months.push(<option key={i}>{('0' + i).slice(-2)}</option>);
		}
		for (let i = new Date().getFullYear(); i < new Date().getFullYear() + 4; i++) {
			expiredYear.push(<option key={i}>{i}</option>);
		}
		return (
			<div style={{ backgroundImage: `url(${background})`, width: '100%', height: '100%' }}>
				<div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
					<Card
						style={{
							marginTop: '40px',
							backgroundColor: '#F3F5F7',
							width: '30rem'
						}}
					>
						<HeaderTransaction />
						<Form style={{ marginTop: '5px' }}>
							<BeneficiaryClient />
							<FormGroup style={{ marginBottom: '10px' }}>
								<Label style={{ color: 'grey' }}>Montant</Label>
								<InputGroup>
									<Input
										type="number"
										name="bill"
										placeholder="Montant"
										required
										onChange={(e) => this.setState({ bill: e.target.value })}
									/>

									<InputGroupAddon addonType="prepend">
										<InputGroupText>€</InputGroupText>
									</InputGroupAddon>
								</InputGroup>
							</FormGroup>
							<FormGroup>
								<Label style={{ color: 'grey' }}>Nom</Label>
								<Input
									type=""
									name="lastName"
									id="lastName"
									placeholder="Nom"
									required
									value={this.state.letterLastNameValue}
									onChange={this.handleChangeLastNameLetter}
								/>
							</FormGroup>
							<FormGroup>
								<Label style={{ color: 'grey' }}>Prénom</Label>
								<Input
									type="firstName"
									name="firstName"
									id="firstName"
									placeholder="Prénom"
									required
									value={this.state.letterFirstNameValue}
									onChange={this.handleChangeFirstNameLetter}
								/>
							</FormGroup>
							<CardItem handleClickParent={this.handleClickCard} idCardFound={this.state.idCardFound} />
							<Label style={{ color: 'grey' }}>Numéro de carte</Label>
							<FormGroup style={{ display: 'flex', paddindRight: '10px' }}>
								<Input
									type="text"
									name="cardNumber"
									id="cardNumber"
									placeholder="Numéro de carte"
									required
									value={this.state.cardNumberValue}
									onChange={this.handleChangeCardNumber}
								/>
								<InputGroupAddon addonType="prepend">
									<InputGroupText>
										<img
											src={require('../images/Visa.png')}
											style={{ width: '64px', height: '40' }}
										/>
									</InputGroupText>
								</InputGroupAddon>
							</FormGroup>
							<div
								className="form-inline"
								style={{ justifyContent: 'space-around', marginBottom: '10px' }}
							>
								<FormGroup style={{ justifyContent: 'flex-start' }}>
									<Label style={{ color: 'grey' }}>Expire fin: &nbsp;</Label>
									<Input
										type="select"
										name="select"
										id="expiredMonth"
										required
										onChange={(e) => this.setState({ expiredMonth: e.target.value })}
									>
										{months}
									</Input>
									&nbsp;/&nbsp;
									<Input
										type="select"
										name="yeexpiredYear"
										id="expiredYear"
										required
										onChange={(e) => this.setState({ expiredYear: e.target.value })}
									>
										{expiredYear}
									</Input>
								</FormGroup>
							</div>
							<FormGroup>
								<Label style={{ color: 'grey' }}>Cryptogramme visuel</Label>
								<Input
									type="text"
									name="cryptoNumber"
									id="cryptoNumber"
									placeholder="Cryptogramme visuel"
									required
									value={this.state.cryptoNumberValue}
									onChange={this.handleChangeryptoNumber}
								/>
							</FormGroup>
							<div style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '30px' }}>
								<Button style={{ backgroundColor: '#C82333', border: 0 }}>Annuler</Button>
								<Button style={{ backgroundColor: '#218838', border: 0 }} onClick={this.handleSubmit}>
									Valider
								</Button>
							</div>
						</Form>
					</Card>
				</div>
			</div>
		);
	}
}
