import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { FormGroup, Label } from 'reactstrap';
import Visa from '../images/Visa.png';
import MasterCard from '../images/Mastercard.png';
import AmericanCard from '../images/American-Express.png';

export default class CardItem extends React.Component {
	render() {
		const cardAvailable = [
			{
				title: 'Visa',
				img: '../images/Visa.png'
			},
			{
				title: 'MasterCard',
				img: '../images/MasterCard.png'
			},
			{
				title: 'AmericanCard',
				img: '../images/American-Express.png'
			}
		];
		let cardList = [];
		for (let i = 0; i < cardAvailable.length; i++) {
			cardList.push(
				<img
					key={i}
					src={cardAvailable[i].img}
					title={cardAvailable[i].title}
					style={{ width: '64px', height: '40' }}
				/>
			);
		}
		return (
			<div>
				<FormGroup>
					<Label style={{ color: 'grey' }}>Votre type de carte</Label>
					<div style={{ display: 'flex', justifyContent: 'space-around' }}>{cardList}</div>
				</FormGroup>
			</div>
		);
	}
}
