import React from 'react';
import { FormGroup, Label, Input } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';

export default class Email extends React.Component {
	constructor() {
		super();
		this.state = { email: '', invalidEmail: false };
	}

	checkEmail = (email) => {
		this.setState({ email: email });
		const mailformat = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
		if (email.match(mailformat)) {
			this.setState({ invalidEmail: false });
			this.props.checkedEmail(email);
		} else {
			this.setState({ invalidEmail: true });
		}
	};

	render() {
		return (
			<div>
				<Label style={{ color: 'grey' }}>Email</Label>
				<FormGroup>
					<Input
						type="email"
						name="email"
						id="Email"
						placeholder="Email"
						onChange={(e) => this.checkEmail(e.target.value)}
						value={this.state.email}
						required
					/>
				</FormGroup>
				{this.state.invalidEmail || this.props.errorEmail ? (
					<p style={{ color: '#C82333', textAlign: 'center' }}>Votre email est invalid</p>
				) : (
					''
				)}
			</div>
		);
	}
}
