import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { FormGroup, Label, Input } from 'reactstrap';

export default class BeneficiaryClient extends React.Component {
	constructor() {
		super();
		this.state = {
			ibanValue: ''
		};
	}
	handleCheckIban = ({ target: { value } }) => {
		value = value
			// Remove all non-digits
			.toUpperCase()
			.replace(/[^A-Z0-9]/g, '')
			.match(/^([A-Z]{2})(\d{2})(.+)$/);
		// Stick to first 16, ignore later digits

		this.setState({ ibanValue: value });
	};
	render() {
		return (
			<FormGroup>
				<Label style={{ color: 'grey' }}>Num√©ro IBAN</Label>
				<Input
					type="text"
					name="iban"
					id="iban"
					placeholder="IBAN"
					required
					value={this.state.ibanValue}
					onChange={this.handleCheckIban}
				/>
			</FormGroup>
		);
	}
}
