import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { Button, Form, FormGroup, Label, Input, Card, InputGroupText, InputGroupAddon, InputGroup } from 'reactstrap';

export default class Amount extends React.Component {
	constructor() {
		super();
		this.state = {};
	}
	handleClick = (title) => {
		this.props.handleClickParent(title);
	};
	render() {
		return (
			<FormGroup style={{ marginBottom: '10px' }}>
				<Label style={{ color: 'grey' }}>Montant</Label>
				<InputGroup>
					<Input
						type="number"
						name="bill"
						placeholder="Montant"
						required
						onChange={(e) => this.setState({ bill: e.target.value })}
					/>

					<InputGroupAddon addonType="prepend">
						<InputGroupText>â‚¬</InputGroupText>
					</InputGroupAddon>
				</InputGroup>
			</FormGroup>
		);
	}
}
