import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { FormGroup, Label, Input, InputGroupAddon, InputGroupText } from 'reactstrap';

export default class BeneficiaryClient extends React.Component {
	constructor() {
		super();
		this.state = {
			ibanValue: ''
		};
	}
	handleCheckIban = ({ target: { value } }) => {
		value = value.toUpperCase();
		// Remove all non-digits

		// Stick to first 16, ignore later digits

		this.setState({ ibanValue: value });
	};
	render() {
		return (
			<div>
				<Label style={{ color: 'grey' }}>Numéro IBAN</Label>
				<div style={{ display: 'flex', justifyContent: 'space-around' }}>
					<InputGroupAddon addonType="prepend">
						<InputGroupText>FR</InputGroupText>
					</InputGroupAddon>
					<FormGroup>
						<Input type="text" name="keyIban" id="keyIban" placeholder="keyIban" maxLength="2" required />
					</FormGroup>
					<FormGroup>
						<Input type="text" name="BankCode" id="BankCode" placeholder="BankCode" required />
					</FormGroup>
					<FormGroup>
						<Input type="text" name="counterCount" id="counterCount" placeholder="Code guichet" required />
					</FormGroup>
					<FormGroup>
						<Input
							type="text"
							name="countNumber"
							id="countNumber"
							placeholder="Numéro de compte"
							required
						/>
					</FormGroup>
					<FormGroup>
						<Input type="text" name="keyRib" id="keyRib" placeholder="Clé rib" required />
					</FormGroup>
				</div>
			</div>
		);
	}
}
