import React from 'react';
import { Card, Button } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';
import HeaderTransaction from './HeaderTransaction';
import PaymentMethod from './PaymentMethod';
import Amount from './Amount';
import CardItem from './CardItem';
import PaiementNumber from './PaiementNumber';
import background from '../images/background.jpg';

export default class Payment extends React.Component {
	constructor() {
		super();
		this.state = {
			card: '',
			amount: '',
			idPsp: '',
			errorMessage: '',
			paymentMode: ''
		};
	}
	handleSubmit = () => {
		if (this.state.card && this.state.amount && this.state.idPsp && this.state.paymentMode) {
			fetch(
				'http://localhost:3000/doTransaction',
				{
					method: 'POST',
					headers: {
						'Content-Type': 'application/x-www-form-urlencoded'
					},
					body: `card=${this.state.card}&amount=${this.state.amount}&idPsp=${this.state
						.idPsp}&paymentMode=${this.state.paymentMode}`
				},
				this.props.history.push('/Customer')
			).catch((error) => {
				console.error(error);
			});
		} else {
			this.setState({
				errorMessage: 'Tout les champs ne sont pas correctement remplis'
			});
		}
	};

	handleClickCardSelected = (card) => {
		console.log('CardSelected:', card);
		this.setState({ card: card });
	};

	handleClickPaymentMode = (paymentMode) => {
		console.log('PaymentMode:', paymentMode);
		this.setState({ paymentMode: paymentMode });
	};

	handleAmount = (amount) => {
		console.log('Montant:', amount);
		this.setState({ amount: amount });
	};

	handleIdPsp = (idPsp) => {
		console.log('idPsp:', idPsp);
		this.setState({ idPsp: idPsp });
	};

	render() {
		const style = {
			display: {
				marginTop: 50,
				textAlign: 'center',
				color: '#d63031'
			}
		};
		return (
			<div style={{ backgroundImage: `url(${background})`, width: '100%', height: '100vh' }}>
				<div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
					<Card
						style={{
							marginTop: '40px',
							backgroundColor: '#F3F5F7',
							width: '30rem'
						}}
					>
						<HeaderTransaction />
						<p
							style={{
								color: '#50C3B9',
								fontWeight: 'bold',
								fontSize: 'large',
								textDecoration: 'underline'
							}}
						>
							Le paiement
						</p>
						<CardItem handleClickCardSelected={this.handleClickCardSelected} />
						<PaymentMethod handleClickPaymentMode={this.handleClickPaymentMode} />
						<Amount handleClickAmount={this.handleAmount} />
						<PaiementNumber handleClickIdPsp={this.handleIdPsp} />
						<div style={{ display: 'flex', justifyContent: 'space-around' }}>
							<Button style={{ backgroundColor: '#C82333', border: 0 }}>Annuler</Button>
							<Button style={{ backgroundColor: '#218838', border: 0 }} onClick={this.handleSubmit}>
								Continuer
							</Button>
						</div>
						<p style={style.display}>{this.state.errorMessage}</p>
					</Card>
				</div>
			</div>
		);
	}
}
