import React from 'react';
import { Card, CardBody, CardTitle, CardSubtitle } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronRight, faAngleRight } from '@fortawesome/free-solid-svg-icons';

export default class SummaryPayment extends React.Component {
	constructor() {
		super();
		this.state = {};
	}

	componentDidMount() {
		fetch(
			'http://localhost:3000/doTransaction',
			{
				method: 'POST',
				headers: {
					'Content-Type': 'application/x-www-form-urlencoded'
				},
				body: `cardtype=${this.props.location.state.card}&amount=${this.props.location.state
					.amount}&idPsp=${this.props.location.state.idPsp}&paymentMode=${this.props.location.state
					.paymentMode}&firstName=${this.state.firstName}&lastName=${this.state.lastName}&email=${this.state
					.email}`
			},
			this.props.history.push('/Summary', this.state)
		).catch((error) => {
			console.error(error);
		});
	}

	render() {
		return (
			<Card style={{ backgroundColor: '#e8e9ea' }}>
				<CardBody style={{ color: 'grey' }}>
					<CardTitle style={{ fontWeight: 'bold', marginBottom: '10px' }}>Le paiement</CardTitle>
					<CardSubtitle style={{ marginBottom: '5px' }}>
						<FontAwesomeIcon size="1x" style={{ marginRight: '5px' }} icon={faAngleRight} />Moyen de
						paiement :
					</CardSubtitle>
					<CardSubtitle style={{ marginBottom: '5px' }}>
						<FontAwesomeIcon size="1x" style={{ marginRight: '5px' }} icon={faAngleRight} />Mode de paiement
						:
					</CardSubtitle>
					<CardSubtitle style={{ marginBottom: '5px' }}>
						<FontAwesomeIcon size="1x" style={{ marginRight: '5px' }} icon={faAngleRight} />Montant :
					</CardSubtitle>
					<CardSubtitle style={{ marginBottom: '5px' }}>
						<FontAwesomeIcon size="1x" style={{ marginRight: '5px' }} icon={faAngleRight} />Client(e) :
					</CardSubtitle>
				</CardBody>
			</Card>
		);
	}
}
