import React from 'react';
import { Table, Card } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';
import background from '../images/background.jpg';

export default class TableTransactions extends React.Component {
	constructor() {
		super();
		this.state = {
			allTransactions: [],
			expiredMonth: '',
			expiredYear: '',
			cardNumber: '',
			cryptoNumber: '',
			idPsp: '',
			total: ''
		};
	}

	componentDidMount() {
		var ctx = this;
		console.log('signin from front handled...');

		fetch(`http://localhost:3000/allTransactions`)
			.then(function(res, err) {
				return res.json();
			})
			.then(function(data) {
				console.log(data);
				ctx.setState({
					allTransactions: data.allTransactions
				});
				console.log(data);
			})
			.catch(function(err) {
				console.log(err);
			});
	}

	render() {
		console.log(this.state.allTransactions);
		let allTransactions = [];
		this.state.allTransactions.map((transaction, i) => {
			allTransactions.push(
				<tr>
					<th scope="row">i</th>
					<td>transaction.firstName</td>
				</tr>
			);
		});

		return (
			<div style={{ backgroundImage: `url(${background})`, width: '100%', height: '100vh' }}>
				<Card
					style={{
						backgroundColor: '#F3F5F7'
					}}
				>
					<p
						style={{
							color: '#50C3B9',
							fontWeight: 'bold',
							fontSize: 'large',
							textDecoration: 'underline'
						}}
					>
						Toutes les transactions
					</p>
					<Table striped>
						<thead>
							<tr>
								<th>ID</th>
								<th>Référencee</th>
								<th>Montant</th>
								<th>Mode de P.</th>
								<th>Moyen de P.</th>
								<th>Statut</th>
								<th>Client</th>
							</tr>
						</thead>
						<tbody>{allTransactions}</tbody>
					</Table>
				</Card>
			</div>
		);
	}
}
