import React from 'react';
import { Card, Button } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';
import HeaderTransaction from './HeaderTransaction';
import Amount from './Amount';
import CardItem from './CardItem';
import PaiementNumber from './PaiementNumber';
import background from '../images/background.jpg';

export default class Payment extends React.Component {
	constructor() {
		super();
		this.state = {
			card: '',
			amount: '',
			idPsp: '',
			errorMessage: ''
		};
	}
	handleSubmit = () => {
		if (this.state.card && this.state.amount && this.state.idPsp) {
			this.props.history.push('/CustomerList');
		} else {
			this.setState({
				errorMessage: 'Tout les champs ne sont pas correctement remplis'
			});
		}
	};

	handleClickCardSelected = (card) => {
		console.log('CardSelected:', card);
		this.setState({ card: card });
	};

	handleAmount = (amount) => {
		console.log('Montant:', amount);
		this.setState({ amount: amount });
	};

	handleIdPsp = (idPsp) => {
		console.log('idPsp:', idPsp);
		this.setState({ idPsp: idPsp });
	};

	render() {
		return (
			<div style={{ backgroundImage: `url(${background})`, width: '100%', height: '100vh' }}>
				<div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
					<Card
						style={{
							marginTop: '40px',
							backgroundColor: '#F3F5F7',
							width: '30rem'
						}}
					>
						<HeaderTransaction />
						<p
							style={{
								color: '#50C3B9',
								fontWeight: 'bold',
								fontSize: 'large',
								textDecoration: 'underline'
							}}
						>
							Le paiement
						</p>
						<CardItem handleClickCardSelected={this.handleClickCardSelected} />
						<Amount handleClickAmount={this.handleAmount} />
						<PaiementNumber handleClickIdPsp={this.handleIdPsp} />
						<div style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '30px' }}>
							<Button style={{ backgroundColor: '#C82333', border: 0 }}>Annuler</Button>
							<Button style={{ backgroundColor: '#218838', border: 0 }} onClick={this.handleSubmit}>
								Continuer
							</Button>
						</div>
					</Card>
				</div>
			</div>
		);
	}
}
