import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { FormGroup, Label, Input, InputGroupText, InputGroupAddon, InputGroup } from 'reactstrap';

export default class Amount extends React.Component {
	constructor() {
		super();
		this.state = { amount: '' };
	}
	handleClickAmount = (amount) => {
		this.setState({ amount: amount });
		this.props.handleClickAmount(amount);
	};
	render() {
		return (
			<FormGroup style={{ marginBottom: '10px' }}>
				<Label style={{ color: 'grey' }}>Montant</Label>
				<InputGroup>
					<Input
						type="number"
						name="bill"
						placeholder="Montant"
						required
						onChange={(e) => this.handleClickAmount(e.target.value)}
						value={this.state.amount}
					/>

					<InputGroupAddon addonType="prepend">
						<InputGroupText>â‚¬</InputGroupText>
					</InputGroupAddon>
				</InputGroup>
				<span style={{ color: 'grey', textAlign: 'center' }}>Montant total de la transaction</span>
			</FormGroup>
		);
	}
}
