import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { Col, Row, Button, Form, FormGroup, Label, Input, InputGroupText } from 'reactstrap';

export default class BeneficiaryClient extends React.Component {
	constructor() {
		super();
		this.state = {
			ibanValue: ''
		};
	}
	handleCheckIban = ({ target: { value } }) => {
		value = value.toUpperCase();
		// Remove all non-digits

		// Stick to first 16, ignore later digits

		this.setState({ ibanValue: value });
	};
	render() {
		return (
			<div>
				<Label style={{ color: 'grey' }}>Numéro IBAN</Label>
				<div style={{ display: 'flex', justifyContent: 'space-around' }}>
					<Row form>
						<Col md={2}>
							<InputGroupText>FR</InputGroupText>
						</Col>
						<Col md={2}>
							<FormGroup>
								<Input
									type="text"
									name="keyIban"
									id="keyIban"
									placeholder="keyIban"
									maxLength="2"
									required
								/>
							</FormGroup>
						</Col>
						<Col md={2}>
							<FormGroup>
								<Input type="text" name="BankCode" id="BankCode" placeholder="BankCode" required />
							</FormGroup>
						</Col>
						<Col md={2}>
							<FormGroup>
								<Input
									type="text"
									name="counterCount"
									id="counterCount"
									placeholder="Code guichet"
									required
								/>
							</FormGroup>
						</Col>
						<Col md={2}>
							<FormGroup>
								<Input
									type="text"
									name="countNumber"
									id="countNumber"
									placeholder="Numéro de compte"
									required
								/>
							</FormGroup>
						</Col>
						<Col md={2}>
							<FormGroup>
								<Input type="text" name="keyRib" id="keyRib" placeholder="Clé rib" required />
							</FormGroup>
						</Col>
					</Row>
				</div>
			</div>
		);
	}
}
